import React, { useRef, useEffect } from 'react'
import { useSprings, animated } from '@react-spring/web'
import useMeasure from 'react-use-measure'
import { useDrag } from 'react-use-gesture'
import clamp from 'lodash.clamp'

import styles from './styles.module.css'


const pages = [
 //fetch your images here with the url api http://localhost/motionjs/motionphp/api.php 

fetch("http://localhost/motionjs/motionphp/api.php#").then(res => res.json()).then(data => {
return data; 
}) 
 ]

function Viewpager() {
  const index = useRef(0)
  const [ref, { width }] = useMeasure()
  const [props, api] = useSprings(
    pages.length,
    i => ({
      x: i * width,
      scale: width === 0 ? 0 : 1,
      display: 'block',
    }),
    [width]
  )
  useEffect(() => {
    const intervalId = setInterval(() => {
      index.current = (index.current + 1) % pages.length
      api.start(i => ({
        x: (i - index.current) * width,
        scale: 1,
        display: 'block',
      }))
    }, 3000)

    return () => clearInterval(intervalId)
  }, [width])
  const bind = useDrag(({ active, movement: [mx], direction: [xDir], distance, cancel }) => {
    if (active && distance > width / 2) {
      index.current = clamp(index.current + (xDir > 0 ? -1 : 1), 0, pages.length - 1)
      cancel()
    }
    api.start(i => {
      if (i < index.current - 1 || i > index.current + 1) return { display: 'none' }
      const x = (i - index.current) * width + (active ? mx : 0)
      const scale = active ? 1 - distance / width / 2 : 1
      return { x, scale, display: 'block' }
    })
  })
  return (
    <div ref={ref} className={styles.wrapper}>
      {props.map(({ x, display, scale }, i) => (
        <animated.div className={styles.page} {...bind()} key={i} style={{ display, x }}>
          <animated.div style={{ scale, backgroundImage: `url(${pages[i]})` }} />
        </animated.div>
      ))}
    </div>
  )
}

export default function App() {
  return (
    <div className={styles.container}>
      <Viewpager />
    </div>
  )
}
