import React, { useRef, useEffect, useState } from 'react';
import { useSprings, animated } from '@react-spring/web';
import useMeasure from 'react-use-measure';
import { useDrag } from 'react-use-gesture';
import clamp from 'lodash.clamp';

import styles from './styles.module.css';

function Viewpager() {
  const index = useRef(0);
  const [ref, { width }] = useMeasure();
  const [images, setImages] = useState([]);
  const [props, api] = useSprings(
    images.length,
    (i) => ({
      x: i * width,
      scale: width === 0 ? 0 : 1,
      display: 'block',
    }),
    [width]
  );

  useEffect(() => {
    fetch('http://localhost/motionjs/motionphp/api.php')
      .then((res) => res.json())
      .then((data) => {
        setImages(data);
      })
      .catch((error) => {
        console.error('Error al obtener las imágenes:', error);
      });
  }, []);

  useEffect(() => {
    const intervalId = setInterval(() => {
      index.current = (index.current + 1) % images.length;
      api.start((i) => ({
        x: (i - index.current) * width,
        scale: 1,
        display: 'block',
      }));
    }, 3000);

    return () => clearInterval(intervalId);
  }, [width, images]);

  const bind = useDrag(({ active, movement: [mx], direction: [xDir], distance, cancel }) => {
    if (active && distance > width / 2) {
      index.current = clamp(index.current + (xDir > 0 ? -1 : 1), 0, images.length - 1);
      cancel();
    }
    api.start((i) => {
      if (i < index.current - 1 || i > index.current + 1) return { display: 'none' };
      const x = (i - index.current) * width + (active ? mx : 0);
      const scale = active ? 1 - distance / width / 2 : 1;
      return { x, scale, display: 'block' };
    });
  });

  return (
    <div ref={ref} className={styles.wrapper}>
      {props.map(({ x, display, scale }, i) => (
        <animated.div className={styles.page} {...bind()} key={i} style={{ display, x }}>
          <animated.div style={{ scale, backgroundImage: `url(${images[i]})` }} />
        </animated.div>
      ))}
    </div>
  );
}

export default function App() {
  return (
    <div className={styles.container}>
      <Viewpager />
    </div>
  );
}
